function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(f){var h=$("#active-addresses");var d=h.find("table");d.find("tbody").remove();for(var c in f){var b=f[c];if(c==0){d.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(d);for(var l in b){var g=b[l];var k=addresses[g];if(!k){continue}var a="";if(k.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var m="";var j=k.addr;if(k.label!=null){j=k.label;m='<span class="hidden-phone"> - '+k.addr+"</span>"}var n='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+j+"</a>"+m+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(n)}d.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var f=[];var g=n.filteredTaints;for(var j in g){var c=g[j];var k=-1;for(var e in c){var m=c[e];for(var d in f){if(d!=k&&$.inArray(e,f[d])){if(k>=0){var l=f.splice(k,1);var h=f.splice(d,1);f.push(l.concat(h))}else{f[d].push(j)}k=d}if(k>=0){break}}}if(k==-1){f.push([j])}}buildTable(f);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};