function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var b=$("#active-addresses");var a=b.find("table");a.find("tbody").remove();for(var c in e){var h=e[c];if(c==0){a.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{a.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(a);for(var n in h){var j=h[n];var k=addresses[j];if(!k){continue}var g="";if(k.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var m=k.addr;if(k.label!=null){m=k.label;f='<span class="hidden-phone"> - '+k.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+m+"</a>"+f+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(l)}a.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var f=[];var g=m.filteredTaints;for(var k in g){var n=g[k];var e=-1;for(var j in n){var c=n[j];for(var d in f){if(d!=e&&$.inArray(j,f[d])){if(e>=0){var l=f.splice(e,1);var h=f.splice(d,1);f.push(l.concat(h))}else{f[d].push(k)}e=d}if(e>=0){break}}}if(e==-1){f.push([k])}}buildTable(f);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};