function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(d){var l=$("#active-addresses");var e=l.find("table");e.find("tbody").remove();for(var k in d){var g=d[k];if(k==0){e.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var b=$("<tbody></tbody>").appendTo(e);for(var c in g){var h=g[c];var n=addresses[h];if(!n){continue}var f="";if(n.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(n.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var m=n.addr;if(n.label!=null){m=n.label;j='<span class="hidden-phone"> - '+n.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+n.addr+'" onclick="showAddressModal(\''+n.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+n.addr+'" target="new">'+m+"</a>"+j+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(n.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';b.append(a)}e.append(b)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var f=[];var n=h.filteredTaints;for(var k in n){var e=n[k];var d=-1;for(var c in e){var j=e[c];for(var m in f){if(m!=d&&$.inArray(c,f[m])){if(d>=0){var l=f.splice(d,1);var g=f.splice(m,1);f.push(l.concat(g))}else{f[m].push(k)}d=m}if(d>=0){break}}}if(d==-1){f.push([k])}}buildTable(f);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};