function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var f=$("#active-addresses");var b=f.find("table");b.find("tbody").remove();for(var n in m){var a=m[n];if(n==0){b.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(b);for(var e in a){var h=a[e];var d=addresses[h];if(!d){continue}var l="";if(d.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var j=d.addr;if(d.label!=null){j=d.label;c='<span class="hidden-phone"> - '+d.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+j+"</a>"+c+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(g)}b.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var j=[];var n=h.filteredTaints;for(var e in n){var d=n[e];var c=-1;for(var f in d){var k=d[f];for(var l in j){if(l!=c&&$.inArray(f,j[l])){if(c>=0){var g=j.splice(c,1);var m=j.splice(l,1);j.push(g.concat(m))}else{j[l].push(e)}c=l}if(c>=0){break}}}if(c==-1){j.push([e])}}buildTable(j);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};