function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var l=$("#active-addresses");var m=l.find("table");m.find("tbody").remove();for(var a in e){var k=e[a];if(a==0){m.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(m);for(var n in k){var j=k[n];var h=addresses[j];if(!h){continue}var c="";if(h.tag==1){c=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){c=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var g=h.addr;if(h.label!=null){g=h.label;b='<span class="hidden-phone"> - '+h.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+g+"</a>"+b+" "+c+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(d)}m.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var f=[];var n=h.filteredTaints;for(var j in n){var l=n[j];var e=-1;for(var m in l){var d=l[m];for(var c in f){if(c!=e&&$.inArray(m,f[c])){if(e>=0){var k=f.splice(e,1);var g=f.splice(c,1);f.push(k.concat(g))}else{f[c].push(j)}e=c}if(e>=0){break}}}if(e==-1){f.push([j])}}buildTable(f);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};