function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var f=$("#active-addresses");var m=f.find("table");m.find("tbody").remove();for(var b in g){var e=g[b];if(b==0){m.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var c=$("<tbody></tbody>").appendTo(m);for(var h in e){var l=e[h];var a=addresses[l];if(!a){continue}var d="";if(a.tag==1){d=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){d=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var n=a.addr;if(a.label!=null){n=a.label;k='<span class="hidden-phone"> - '+a.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+n+"</a>"+k+" "+d+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';c.append(j)}m.append(c)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var f=[];var m=j.filteredTaints;for(var l in m){var e=m[l];var c=-1;for(var k in e){var g=e[k];for(var d in f){if(d!=c&&$.inArray(k,f[d])){if(c>=0){var n=f.splice(c,1);var h=f.splice(d,1);f.push(n.concat(h))}else{f[d].push(l)}c=d}if(c>=0){break}}}if(c==-1){f.push([l])}}buildTable(f);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};