function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var a=$("#active-addresses");var h=a.find("table");h.find("tbody").remove();for(var e in m){var k=m[e];if(e==0){h.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(h);for(var g in k){var l=k[g];var d=addresses[l];if(!d){continue}var b="";if(d.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var f=d.addr;if(d.label!=null){f=d.label;j='<span class="hidden-phone"> - '+d.addr+"</span>"}var c='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+f+"</a>"+j+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(c)}h.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var n=[];var f=c.filteredTaints;for(var m in f){var k=f[m];var l=-1;for(var h in k){var d=k[h];for(var g in n){if(g!=l&&$.inArray(h,n[g])){if(l>=0){var j=n.splice(l,1);var e=n.splice(g,1);n.push(j.concat(e))}else{n[g].push(m)}l=g}if(l>=0){break}}}if(l==-1){n.push([m])}}buildTable(n);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};