function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var a=$("#active-addresses");var d=a.find("table");d.find("tbody").remove();for(var k in m){var l=m[k];if(k==0){d.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var b=$("<tbody></tbody>").appendTo(d);for(var j in l){var e=l[j];var g=addresses[e];if(!g){continue}var f="";if(g.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(g.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var n=g.addr;if(g.label!=null){n=g.label;c='<span class="hidden-phone"> - '+g.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+g.addr+'" onclick="showAddressModal(\''+g.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+g.addr+'" target="new">'+n+"</a>"+c+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(g.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';b.append(h)}d.append(b)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var m=[];var n=c.filteredTaints;for(var e in n){var l=n[e];var f=-1;for(var h in l){var g=l[h];for(var j in m){if(j!=f&&$.inArray(h,m[j])){if(f>=0){var k=m.splice(f,1);var d=m.splice(j,1);m.push(k.concat(d))}else{m[j].push(e)}f=j}if(f>=0){break}}}if(f==-1){m.push([e])}}buildTable(m);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(m)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};