function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var e=$("#active-addresses");var k=e.find("table");k.find("tbody").remove();for(var l in n){var d=n[l];if(l==0){k.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var c=$("<tbody></tbody>").appendTo(k);for(var m in d){var h=d[m];var b=addresses[h];if(!b){continue}var a="";if(b.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var g=b.addr;if(b.label!=null){g=b.label;f='<span class="hidden-phone"> - '+b.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+g+"</a>"+f+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';c.append(j)}k.append(c)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var n=[];var e=h.filteredTaints;for(var l in e){var g=e[l];var j=-1;for(var k in g){var d=g[k];for(var m in n){if(m!=j&&$.inArray(k,n[m])){if(j>=0){var f=n.splice(j,1);var c=n.splice(m,1);n.push(f.concat(c))}else{n[m].push(l)}j=m}if(j>=0){break}}}if(j==-1){n.push([l])}}buildTable(n);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};