function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(k){var h=$("#active-addresses");var j=h.find("table");j.find("tbody").remove();for(var a in k){var e=k[a];if(a==0){j.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(j);for(var m in e){var n=e[m];var d=addresses[n];if(!d){continue}var b="";if(d.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var f=d.addr;if(d.label!=null){f=d.label;c='<span class="hidden-phone"> - '+d.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+f+"</a>"+c+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(l)}j.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var k=[];var n=f.filteredTaints;for(var m in n){var l=n[m];var c=-1;for(var g in l){var j=l[g];for(var d in k){if(d!=c&&$.inArray(g,k[d])){if(c>=0){var h=k.splice(c,1);var e=k.splice(d,1);k.push(h.concat(e))}else{k[d].push(m)}c=d}if(c>=0){break}}}if(c==-1){k.push([m])}}buildTable(k);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};