function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var e=$("#active-addresses");var k=e.find("table");k.find("tbody").remove();for(var j in c){var l=c[j];if(j==0){k.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(k);for(var b in l){var f=l[b];var n=addresses[f];if(!n){continue}var g="";if(n.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(n.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var m="";var a=n.addr;if(n.label!=null){a=n.label;m='<span class="hidden-phone"> - '+n.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+n.addr+'" onclick="showAddressModal(\''+n.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+n.addr+'" target="new">'+a+"</a>"+m+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(n.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(d)}k.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var e=[];var h=d.filteredTaints;for(var g in h){var n=h[g];var f=-1;for(var m in n){var j=n[m];for(var l in e){if(l!=f&&$.inArray(m,e[l])){if(f>=0){var c=e.splice(f,1);var k=e.splice(l,1);e.push(c.concat(k))}else{e[l].push(g)}f=l}if(f>=0){break}}}if(f==-1){e.push([g])}}buildTable(e);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};