function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var l=$("#active-addresses");var d=l.find("table");d.find("tbody").remove();for(var n in g){var f=g[n];if(n==0){d.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(d);for(var h in f){var c=f[h];var b=addresses[c];if(!b){continue}var m="";if(b.tag==1){m=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){m=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var a=b.addr;if(b.label!=null){a=b.label;k='<span class="hidden-phone"> - '+b.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+a+"</a>"+k+" "+m+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(j)}d.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var j=[];var h=n.filteredTaints;for(var c in h){var d=h[c];var f=-1;for(var l in d){var g=d[l];for(var k in j){if(k!=f&&$.inArray(l,j[k])){if(f>=0){var e=j.splice(f,1);var m=j.splice(k,1);j.push(e.concat(m))}else{j[k].push(c)}f=k}if(f>=0){break}}}if(f==-1){j.push([c])}}buildTable(j);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};