function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(h){var n=$("#active-addresses");var f=n.find("table");f.find("tbody").remove();for(var b in h){var g=h[b];if(b==0){f.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(f);for(var m in g){var e=g[m];var d=addresses[e];if(!d){continue}var a="";if(d.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var c=d.addr;if(d.label!=null){c=d.label;k='<span class="hidden-phone"> - '+d.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+c+"</a>"+k+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(l)}f.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var h=[];var l=f.filteredTaints;for(var e in l){var j=l[e];var k=-1;for(var m in j){var d=j[m];for(var c in h){if(c!=k&&$.inArray(m,h[c])){if(k>=0){var n=h.splice(k,1);var g=h.splice(c,1);h.push(n.concat(g))}else{h[c].push(e)}k=c}if(k>=0){break}}}if(k==-1){h.push([e])}}buildTable(h);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};