function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var f=$("#active-addresses");var l=f.find("table");l.find("tbody").remove();for(var j in m){var h=m[j];if(j==0){l.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(l);for(var n in h){var c=h[n];var e=addresses[c];if(!e){continue}var b="";if(e.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(e.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var d=e.addr;if(e.label!=null){d=e.label;a='<span class="hidden-phone"> - '+e.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+e.addr+'" onclick="showAddressModal(\''+e.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+e.addr+'" target="new">'+d+"</a>"+a+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(e.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(k)}l.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var l=[];var d=h.filteredTaints;for(var e in d){var m=d[e];var j=-1;for(var f in m){var c=m[f];for(var k in l){if(k!=j&&$.inArray(f,l[k])){if(j>=0){var n=l.splice(j,1);var g=l.splice(k,1);l.push(n.concat(g))}else{l[k].push(e)}j=k}if(j>=0){break}}}if(j==-1){l.push([e])}}buildTable(l);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};