function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var a=$("#active-addresses");var n=a.find("table");n.find("tbody").remove();for(var e in c){var j=c[e];if(e==0){n.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(n);for(var d in j){var f=j[d];var m=addresses[f];if(!m){continue}var l="";if(m.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var g=m.addr;if(m.label!=null){g=m.label;b='<span class="hidden-phone"> - '+m.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+g+"</a>"+b+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(k)}n.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var d=[];var c=m.filteredTaints;for(var j in c){var h=c[j];var k=-1;for(var l in h){var n=h[l];for(var g in d){if(g!=k&&$.inArray(l,d[g])){if(k>=0){var e=d.splice(k,1);var f=d.splice(g,1);d.push(e.concat(f))}else{d[g].push(j)}k=g}if(k>=0){break}}}if(k==-1){d.push([j])}}buildTable(d);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};