function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var f=$("#active-addresses");var j=f.find("table");j.find("tbody").remove();for(var l in c){var g=c[l];if(l==0){j.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(j);for(var m in g){var n=g[m];var b=addresses[n];if(!b){continue}var d="";if(b.tag==1){d=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){d=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var h=b.addr;if(b.label!=null){h=b.label;e='<span class="hidden-phone"> - '+b.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+h+"</a>"+e+" "+d+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(a)}j.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var e=[];var h=n.filteredTaints;for(var k in h){var m=h[k];var g=-1;for(var d in m){var f=m[d];for(var j in e){if(j!=g&&$.inArray(d,e[j])){if(g>=0){var c=e.splice(g,1);var l=e.splice(j,1);e.push(c.concat(l))}else{e[j].push(k)}g=j}if(g>=0){break}}}if(g==-1){e.push([k])}}buildTable(e);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};