function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var e=$("#active-addresses");var m=e.find("table");m.find("tbody").remove();for(var k in j){var b=j[k];if(k==0){m.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(m);for(var n in b){var c=b[n];var a=addresses[c];if(!a){continue}var d="";if(a.tag==1){d=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){d=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var f=a.addr;if(a.label!=null){f=a.label;g='<span class="hidden-phone"> - '+a.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+f+"</a>"+g+" "+d+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(h)}m.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var k=[];var d=e.filteredTaints;for(var f in d){var g=d[f];var c=-1;for(var j in g){var n=g[j];for(var l in k){if(l!=c&&$.inArray(j,k[l])){if(c>=0){var m=k.splice(c,1);var h=k.splice(l,1);k.push(m.concat(h))}else{k[l].push(f)}c=l}if(c>=0){break}}}if(c==-1){k.push([f])}}buildTable(k);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};