function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var d=$("#active-addresses");var j=d.find("table");j.find("tbody").remove();for(var h in e){var b=e[h];if(h==0){j.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(j);for(var g in b){var k=b[g];var n=addresses[k];if(!n){continue}var f="";if(n.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(n.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var l=n.addr;if(n.label!=null){l=n.label;c='<span class="hidden-phone"> - '+n.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+n.addr+'" onclick="showAddressModal(\''+n.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+n.addr+'" target="new">'+l+"</a>"+c+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(n.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(m)}j.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var e=[];var c=d.filteredTaints;for(var n in c){var j=c[n];var h=-1;for(var l in j){var m=j[l];for(var k in e){if(k!=h&&$.inArray(l,e[k])){if(h>=0){var g=e.splice(h,1);var f=e.splice(k,1);e.push(g.concat(f))}else{e[k].push(n)}h=k}if(h>=0){break}}}if(h==-1){e.push([n])}}buildTable(e);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};