function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var h=$("#active-addresses");var c=h.find("table");c.find("tbody").remove();for(var b in g){var k=g[b];if(b==0){c.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{c.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(c);for(var n in k){var j=k[n];var d=addresses[j];if(!d){continue}var f="";if(d.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var a=d.addr;if(d.label!=null){a=d.label;e='<span class="hidden-phone"> - '+d.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+a+"</a>"+e+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(m)}c.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var h=[];var j=d.filteredTaints;for(var k in j){var f=j[k];var n=-1;for(var e in f){var l=f[e];for(var c in h){if(c!=n&&$.inArray(e,h[c])){if(n>=0){var g=h.splice(n,1);var m=h.splice(c,1);h.push(g.concat(m))}else{h[c].push(k)}n=c}if(n>=0){break}}}if(n==-1){h.push([k])}}buildTable(h);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};