function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var e=$("#active-addresses");var b=e.find("table");b.find("tbody").remove();for(var l in a){var j=a[l];if(l==0){b.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(b);for(var k in j){var g=j[k];var c=addresses[g];if(!c){continue}var n="";if(c.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var m=c.addr;if(c.label!=null){m=c.label;f='<span class="hidden-phone"> - '+c.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+m+"</a>"+f+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(d)}b.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var c=[];var n=f.filteredTaints;for(var j in n){var d=n[j];var l=-1;for(var h in d){var k=d[h];for(var m in c){if(m!=l&&$.inArray(h,c[m])){if(l>=0){var g=c.splice(l,1);var e=c.splice(m,1);c.push(g.concat(e))}else{c[m].push(j)}l=m}if(l>=0){break}}}if(l==-1){c.push([j])}}buildTable(c);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};