function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var f=$("#active-addresses");var m=f.find("table");m.find("tbody").remove();for(var l in g){var h=g[l];if(l==0){m.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(m);for(var e in h){var c=h[e];var n=addresses[c];if(!n){continue}var d="";if(n.tag==1){d=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(n.priv==null){d=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var b=n.addr;if(n.label!=null){b=n.label;j='<span class="hidden-phone"> - '+n.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+n.addr+'" onclick="showAddressModal(\''+n.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+n.addr+'" target="new">'+b+"</a>"+j+" "+d+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(n.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(a)}m.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var j=[];var m=h.filteredTaints;for(var c in m){var d=m[c];var f=-1;for(var g in d){var e=d[g];for(var k in j){if(k!=f&&$.inArray(g,j[k])){if(f>=0){var l=j.splice(f,1);var n=j.splice(k,1);j.push(l.concat(n))}else{j[k].push(c)}f=k}if(f>=0){break}}}if(f==-1){j.push([c])}}buildTable(j);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};