function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var l=$("#active-addresses");var c=l.find("table");c.find("tbody").remove();for(var h in e){var f=e[h];if(h==0){c.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{c.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(c);for(var n in f){var b=f[n];var m=addresses[b];if(!m){continue}var g="";if(m.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var d=m.addr;if(m.label!=null){d=m.label;k='<span class="hidden-phone"> - '+m.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+d+"</a>"+k+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(j)}c.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var j=[];var e=n.filteredTaints;for(var c in e){var d=e[c];var g=-1;for(var l in d){var k=d[l];for(var m in j){if(m!=g&&$.inArray(l,j[m])){if(g>=0){var f=j.splice(g,1);var h=j.splice(m,1);j.push(f.concat(h))}else{j[m].push(c)}g=m}if(g>=0){break}}}if(g==-1){j.push([c])}}buildTable(j);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};