function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(f){var n=$("#active-addresses");var d=n.find("table");d.find("tbody").remove();for(var c in f){var m=f[c];if(c==0){d.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(d);for(var b in m){var e=m[b];var l=addresses[e];if(!l){continue}var j="";if(l.tag==1){j=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){j=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var k=l.addr;if(l.label!=null){k=l.label;a='<span class="hidden-phone"> - '+l.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+k+"</a>"+a+" "+j+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(g)}d.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(k){var h=[];var c=k.filteredTaints;for(var g in c){var m=c[g];var l=-1;for(var n in m){var e=m[n];for(var d in h){if(d!=l&&$.inArray(n,h[d])){if(l>=0){var j=h.splice(l,1);var f=h.splice(d,1);h.push(j.concat(f))}else{h[d].push(g)}l=d}if(l>=0){break}}}if(l==-1){h.push([g])}}buildTable(h);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};