function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var a=$("#active-addresses");var n=a.find("table");n.find("tbody").remove();for(var l in e){var c=e[l];if(l==0){n.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(n);for(var g in c){var k=c[g];var b=addresses[k];if(!b){continue}var f="";if(b.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var m=b.addr;if(b.label!=null){m=b.label;h='<span class="hidden-phone"> - '+b.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+m+"</a>"+h+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(d)}n.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var g=[];var h=n.filteredTaints;for(var l in h){var k=h[l];var e=-1;for(var d in k){var c=k[d];for(var m in g){if(m!=e&&$.inArray(d,g[m])){if(e>=0){var j=g.splice(e,1);var f=g.splice(m,1);g.push(j.concat(f))}else{g[m].push(l)}e=m}if(e>=0){break}}}if(e==-1){g.push([l])}}buildTable(g);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};