function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var c=$("#active-addresses");var l=c.find("table");l.find("tbody").remove();for(var f in g){var j=g[f];if(f==0){l.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(l);for(var e in j){var b=j[e];var n=addresses[b];if(!n){continue}var m="";if(n.tag==1){m=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(n.priv==null){m=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var h=n.addr;if(n.label!=null){h=n.label;a='<span class="hidden-phone"> - '+n.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+n.addr+'" onclick="showAddressModal(\''+n.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+n.addr+'" target="new">'+h+"</a>"+a+" "+m+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(n.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(k)}l.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var k=[];var e=n.filteredTaints;for(var c in e){var m=e[c];var j=-1;for(var l in m){var d=m[l];for(var h in k){if(h!=j&&$.inArray(l,k[h])){if(j>=0){var g=k.splice(j,1);var f=k.splice(h,1);k.push(g.concat(f))}else{k[h].push(c)}j=h}if(j>=0){break}}}if(j==-1){k.push([c])}}buildTable(k);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};