function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var b=$("#active-addresses");var f=b.find("table");f.find("tbody").remove();for(var l in n){var d=n[l];if(l==0){f.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(f);for(var m in d){var j=d[m];var h=addresses[j];if(!h){continue}var e="";if(h.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var g=h.addr;if(h.label!=null){g=h.label;c='<span class="hidden-phone"> - '+h.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+g+"</a>"+c+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(a)}f.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var m=[];var k=n.filteredTaints;for(var h in k){var c=k[h];var f=-1;for(var d in c){var e=c[d];for(var j in m){if(j!=f&&$.inArray(d,m[j])){if(f>=0){var g=m.splice(f,1);var l=m.splice(j,1);m.push(g.concat(l))}else{m[j].push(h)}f=j}if(f>=0){break}}}if(f==-1){m.push([h])}}buildTable(m);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(m)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};