function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var n=$("#active-addresses");var j=n.find("table");j.find("tbody").remove();for(var f in e){var a=e[f];if(f==0){j.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(j);for(var b in a){var c=a[b];var d=addresses[c];if(!d){continue}var l="";if(d.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var g=d.addr;if(d.label!=null){g=d.label;h='<span class="hidden-phone"> - '+d.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+g+"</a>"+h+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(m)}j.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var j=[];var c=d.filteredTaints;for(var e in c){var f=c[e];var n=-1;for(var l in f){var g=f[l];for(var k in j){if(k!=n&&$.inArray(l,j[k])){if(n>=0){var m=j.splice(n,1);var h=j.splice(k,1);j.push(m.concat(h))}else{j[k].push(e)}n=k}if(n>=0){break}}}if(n==-1){j.push([e])}}buildTable(j);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};