function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(h){var k=$("#active-addresses");var e=k.find("table");e.find("tbody").remove();for(var n in h){var d=h[n];if(n==0){e.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(e);for(var l in d){var c=d[l];var a=addresses[c];if(!a){continue}var j="";if(a.tag==1){j=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){j=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var g=a.addr;if(a.label!=null){g=a.label;b='<span class="hidden-phone"> - '+a.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+g+"</a>"+b+" "+j+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(m)}e.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var l=[];var c=e.filteredTaints;for(var f in c){var j=c[f];var g=-1;for(var d in j){var h=j[d];for(var n in l){if(n!=g&&$.inArray(d,l[n])){if(g>=0){var m=l.splice(g,1);var k=l.splice(n,1);l.push(m.concat(k))}else{l[n].push(f)}g=n}if(g>=0){break}}}if(g==-1){l.push([f])}}buildTable(l);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};