function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var a=$("#active-addresses");var h=a.find("table");h.find("tbody").remove();for(var n in e){var j=e[n];if(n==0){h.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(h);for(var l in j){var m=j[l];var k=addresses[m];if(!k){continue}var d="";if(k.tag==1){d=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){d=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var b=k.addr;if(k.label!=null){b=k.label;c='<span class="hidden-phone"> - '+k.addr+"</span>"}var f='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+b+"</a>"+c+" "+d+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(f)}h.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var d=[];var j=h.filteredTaints;for(var n in j){var l=j[n];var e=-1;for(var k in l){var c=l[k];for(var m in d){if(m!=e&&$.inArray(k,d[m])){if(e>=0){var f=d.splice(e,1);var g=d.splice(m,1);d.push(f.concat(g))}else{d[m].push(n)}e=m}if(e>=0){break}}}if(e==-1){d.push([n])}}buildTable(d);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};