function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var f=$("#active-addresses");var h=f.find("table");h.find("tbody").remove();for(var m in a){var e=a[m];if(m==0){h.append('<tbody><tr><th colspan="2">Group #'+m+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+m+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(h);for(var c in e){var l=e[c];var b=addresses[l];if(!b){continue}var j="";if(b.tag==1){j=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){j=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var n=b.addr;if(b.label!=null){n=b.label;g='<span class="hidden-phone"> - '+b.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+n+"</a>"+g+" "+j+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(d)}h.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var e=[];var h=d.filteredTaints;for(var k in h){var j=h[k];var c=-1;for(var g in j){var m=j[g];for(var l in e){if(l!=c&&$.inArray(g,e[l])){if(c>=0){var n=e.splice(c,1);var f=e.splice(l,1);e.push(n.concat(f))}else{e[l].push(k)}c=l}if(c>=0){break}}}if(c==-1){e.push([k])}}buildTable(e);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};