function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var h=$("#active-addresses");var j=h.find("table");j.find("tbody").remove();for(var e in a){var f=a[e];if(e==0){j.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(j);for(var n in f){var k=f[n];var m=addresses[k];if(!m){continue}var d="";if(m.tag==1){d=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){d=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var c=m.addr;if(m.label!=null){c=m.label;b='<span class="hidden-phone"> - '+m.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+c+"</a>"+b+" "+d+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(g)}j.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var d=[];var l=j.filteredTaints;for(var n in l){var m=l[n];var c=-1;for(var e in m){var k=m[e];for(var h in d){if(h!=c&&$.inArray(e,d[h])){if(c>=0){var g=d.splice(c,1);var f=d.splice(h,1);d.push(g.concat(f))}else{d[h].push(n)}c=h}if(c>=0){break}}}if(c==-1){d.push([n])}}buildTable(d);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};