function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var h=$("#active-addresses");var l=h.find("table");l.find("tbody").remove();for(var k in c){var b=c[k];if(k==0){l.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(l);for(var f in b){var d=b[f];var m=addresses[d];if(!m){continue}var g="";if(m.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var j=m.addr;if(m.label!=null){j=m.label;a='<span class="hidden-phone"> - '+m.addr+"</span>"}var e='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+j+"</a>"+a+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(e)}l.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var c=[];var d=m.filteredTaints;for(var f in d){var e=d[f];var h=-1;for(var g in e){var n=e[g];for(var k in c){if(k!=h&&$.inArray(g,c[k])){if(h>=0){var l=c.splice(h,1);var j=c.splice(k,1);c.push(l.concat(j))}else{c[k].push(f)}h=k}if(h>=0){break}}}if(h==-1){c.push([f])}}buildTable(c);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};