function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var l=$("#active-addresses");var a=l.find("table");a.find("tbody").remove();for(var k in e){var g=e[k];if(k==0){a.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{a.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(a);for(var f in g){var m=g[f];var j=addresses[m];if(!j){continue}var h="";if(j.tag==1){h=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){h=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var n="";var c=j.addr;if(j.label!=null){c=j.label;n='<span class="hidden-phone"> - '+j.addr+"</span>"}var b='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+c+"</a>"+n+" "+h+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(b)}a.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var g=[];var j=d.filteredTaints;for(var m in j){var e=j[m];var f=-1;for(var l in e){var n=e[l];for(var k in g){if(k!=f&&$.inArray(l,g[k])){if(f>=0){var c=g.splice(f,1);var h=g.splice(k,1);g.push(c.concat(h))}else{g[k].push(m)}f=k}if(f>=0){break}}}if(f==-1){g.push([m])}}buildTable(g);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};