function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(k){var n=$("#active-addresses");var c=n.find("table");c.find("tbody").remove();for(var d in k){var a=k[d];if(d==0){c.append('<tbody><tr><th colspan="2">Group #'+d+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{c.append('<tbody><tr><th colspan="4">Group #'+d+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(c);for(var l in a){var b=a[l];var j=addresses[b];if(!j){continue}var m="";if(j.tag==1){m=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){m=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var g=j.addr;if(j.label!=null){g=j.label;h='<span class="hidden-phone"> - '+j.addr+"</span>"}var f='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+g+"</a>"+h+" "+m+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(f)}c.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var l=[];var d=j.filteredTaints;for(var c in d){var g=d[c];var h=-1;for(var m in g){var k=g[m];for(var f in l){if(f!=h&&$.inArray(m,l[f])){if(h>=0){var n=l.splice(h,1);var e=l.splice(f,1);l.push(n.concat(e))}else{l[f].push(c)}h=f}if(h>=0){break}}}if(h==-1){l.push([c])}}buildTable(l);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};