function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var l=$("#active-addresses");var e=l.find("table");e.find("tbody").remove();for(var c in a){var f=a[c];if(c==0){e.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(e);for(var b in f){var g=f[b];var k=addresses[g];if(!k){continue}var h="";if(k.tag==1){h=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){h=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var n=k.addr;if(k.label!=null){n=k.label;j='<span class="hidden-phone"> - '+k.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+n+"</a>"+j+" "+h+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(d)}e.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var d=[];var k=n.filteredTaints;for(var j in k){var m=k[j];var h=-1;for(var l in m){var e=m[l];for(var f in d){if(f!=h&&$.inArray(l,d[f])){if(h>=0){var c=d.splice(h,1);var g=d.splice(f,1);d.push(c.concat(g))}else{d[f].push(j)}h=f}if(h>=0){break}}}if(h==-1){d.push([j])}}buildTable(d);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};