function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var c=$("#active-addresses");var m=c.find("table");m.find("tbody").remove();for(var l in g){var n=g[l];if(l==0){m.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(m);for(var d in n){var f=n[d];var j=addresses[f];if(!j){continue}var e="";if(j.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var a=j.addr;if(j.label!=null){a=j.label;b='<span class="hidden-phone"> - '+j.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+a+"</a>"+b+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(h)}m.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var j=[];var f=e.filteredTaints;for(var g in f){var c=f[g];var k=-1;for(var n in c){var d=c[n];for(var m in j){if(m!=k&&$.inArray(n,j[m])){if(k>=0){var h=j.splice(k,1);var l=j.splice(m,1);j.push(h.concat(l))}else{j[m].push(g)}k=m}if(k>=0){break}}}if(k==-1){j.push([g])}}buildTable(j);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};