function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var l=$("#active-addresses");var j=l.find("table");j.find("tbody").remove();for(var f in c){var a=c[f];if(f==0){j.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(j);for(var m in a){var e=a[m];var k=addresses[e];if(!k){continue}var b="";if(k.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var n=k.addr;if(k.label!=null){n=k.label;g='<span class="hidden-phone"> - '+k.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+n+"</a>"+g+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(h)}j.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var d=[];var j=h.filteredTaints;for(var g in j){var k=j[g];var e=-1;for(var n in k){var l=k[n];for(var f in d){if(f!=e&&$.inArray(n,d[f])){if(e>=0){var c=d.splice(e,1);var m=d.splice(f,1);d.push(c.concat(m))}else{d[f].push(g)}e=f}if(e>=0){break}}}if(e==-1){d.push([g])}}buildTable(d);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};