function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(k){var d=$("#active-addresses");var l=d.find("table");l.find("tbody").remove();for(var f in k){var j=k[f];if(f==0){l.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(l);for(var c in j){var h=j[c];var m=addresses[h];if(!m){continue}var g="";if(m.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var e=m.addr;if(m.label!=null){e=m.label;b='<span class="hidden-phone"> - '+m.addr+"</span>"}var n='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+e+"</a>"+b+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(n)}l.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(k){var l=[];var n=k.filteredTaints;for(var j in n){var m=n[j];var e=-1;for(var d in m){var f=m[d];for(var g in l){if(g!=e&&$.inArray(d,l[g])){if(e>=0){var c=l.splice(e,1);var h=l.splice(g,1);l.push(c.concat(h))}else{l[g].push(j)}e=g}if(e>=0){break}}}if(e==-1){l.push([j])}}buildTable(l);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};