function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var l=$("#active-addresses");var c=l.find("table");c.find("tbody").remove();for(var m in g){var f=g[m];if(m==0){c.append('<tbody><tr><th colspan="2">Group #'+m+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{c.append('<tbody><tr><th colspan="4">Group #'+m+"</th></tr></tbody>")}var b=$("<tbody></tbody>").appendTo(c);for(var n in f){var h=f[n];var a=addresses[h];if(!a){continue}var e="";if(a.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var j=a.addr;if(a.label!=null){j=a.label;k='<span class="hidden-phone"> - '+a.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+j+"</a>"+k+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';b.append(d)}c.append(b)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(k){var g=[];var e=k.filteredTaints;for(var h in e){var c=e[h];var d=-1;for(var j in c){var n=c[j];for(var m in g){if(m!=d&&$.inArray(j,g[m])){if(d>=0){var l=g.splice(d,1);var f=g.splice(m,1);g.push(l.concat(f))}else{g[m].push(h)}d=m}if(d>=0){break}}}if(d==-1){g.push([h])}}buildTable(g);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};