function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var j=$("#active-addresses");var h=j.find("table");h.find("tbody").remove();for(var d in l){var k=l[d];if(d==0){h.append('<tbody><tr><th colspan="2">Group #'+d+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+d+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(h);for(var e in k){var g=k[e];var c=addresses[g];if(!c){continue}var n="";if(c.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var m=c.addr;if(c.label!=null){m=c.label;b='<span class="hidden-phone"> - '+c.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+m+"</a>"+b+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(a)}h.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var l=[];var m=g.filteredTaints;for(var j in m){var k=m[j];var h=-1;for(var e in k){var d=k[e];for(var f in l){if(f!=h&&$.inArray(e,l[f])){if(h>=0){var n=l.splice(h,1);var c=l.splice(f,1);l.push(n.concat(c))}else{l[f].push(j)}h=f}if(h>=0){break}}}if(h==-1){l.push([j])}}buildTable(l);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};