function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var g=$("#active-addresses");var n=g.find("table");n.find("tbody").remove();for(var b in j){var m=j[b];if(b==0){n.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(n);for(var c in m){var f=m[c];var k=addresses[f];if(!k){continue}var l="";if(k.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var d="";var e=k.addr;if(k.label!=null){e=k.label;d='<span class="hidden-phone"> - '+k.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+e+"</a>"+d+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(a)}n.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var k=[];var m=h.filteredTaints;for(var j in m){var g=m[j];var l=-1;for(var d in g){var e=g[d];for(var c in k){if(c!=l&&$.inArray(d,k[c])){if(l>=0){var n=k.splice(l,1);var f=k.splice(c,1);k.push(n.concat(f))}else{k[c].push(j)}l=c}if(l>=0){break}}}if(l==-1){k.push([j])}}buildTable(k);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};