function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var b=$("#active-addresses");var c=b.find("table");c.find("tbody").remove();for(var e in n){var a=n[e];if(e==0){c.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{c.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(c);for(var l in a){var j=a[l];var h=addresses[j];if(!h){continue}var g="";if(h.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var f=h.addr;if(h.label!=null){f=h.label;k='<span class="hidden-phone"> - '+h.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+f+"</a>"+k+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(d)}c.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var n=[];var d=j.filteredTaints;for(var l in d){var e=d[l];var m=-1;for(var h in e){var k=e[h];for(var c in n){if(c!=m&&$.inArray(h,n[c])){if(m>=0){var f=n.splice(m,1);var g=n.splice(c,1);n.push(f.concat(g))}else{n[c].push(l)}m=c}if(m>=0){break}}}if(m==-1){n.push([l])}}buildTable(n);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};