function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var k=$("#active-addresses");var h=k.find("table");h.find("tbody").remove();for(var f in a){var e=a[f];if(f==0){h.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(h);for(var m in e){var n=e[m];var b=addresses[n];if(!b){continue}var c="";if(b.tag==1){c=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){c=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var d="";var j=b.addr;if(b.label!=null){j=b.label;d='<span class="hidden-phone"> - '+b.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+j+"</a>"+d+" "+c+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(l)}h.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var c=[];var e=g.filteredTaints;for(var m in e){var l=e[m];var n=-1;for(var j in l){var k=l[j];for(var f in c){if(f!=n&&$.inArray(j,c[f])){if(n>=0){var d=c.splice(n,1);var h=c.splice(f,1);c.push(d.concat(h))}else{c[f].push(m)}n=f}if(n>=0){break}}}if(n==-1){c.push([m])}}buildTable(c);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};