function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(k){var c=$("#active-addresses");var e=c.find("table");e.find("tbody").remove();for(var b in k){var m=k[b];if(b==0){e.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(e);for(var a in m){var g=m[a];var n=addresses[g];if(!n){continue}var l="";if(n.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(n.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var f=n.addr;if(n.label!=null){f=n.label;j='<span class="hidden-phone"> - '+n.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+n.addr+'" onclick="showAddressModal(\''+n.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+n.addr+'" target="new">'+f+"</a>"+j+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(n.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(h)}e.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var j=[];var g=e.filteredTaints;for(var f in g){var k=g[f];var m=-1;for(var n in k){var l=k[n];for(var c in j){if(c!=m&&$.inArray(n,j[c])){if(m>=0){var h=j.splice(m,1);var d=j.splice(c,1);j.push(h.concat(d))}else{j[c].push(f)}m=c}if(m>=0){break}}}if(m==-1){j.push([f])}}buildTable(j);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};