function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var n=$("#active-addresses");var d=n.find("table");d.find("tbody").remove();for(var m in c){var f=c[m];if(m==0){d.append('<tbody><tr><th colspan="2">Group #'+m+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+m+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(d);for(var g in f){var l=f[g];var k=addresses[l];if(!k){continue}var b="";if(k.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var h=k.addr;if(k.label!=null){h=k.label;a='<span class="hidden-phone"> - '+k.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+h+"</a>"+a+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(j)}d.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var e=[];var c=m.filteredTaints;for(var j in c){var d=c[j];var l=-1;for(var h in d){var g=d[h];for(var k in e){if(k!=l&&$.inArray(h,e[k])){if(l>=0){var n=e.splice(l,1);var f=e.splice(k,1);e.push(n.concat(f))}else{e[k].push(j)}l=k}if(l>=0){break}}}if(l==-1){e.push([j])}}buildTable(e);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};