function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var m=$("#active-addresses");var d=m.find("table");d.find("tbody").remove();for(var a in l){var n=l[a];if(a==0){d.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(d);for(var e in n){var f=n[e];var k=addresses[f];if(!k){continue}var h="";if(k.tag==1){h=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){h=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var c=k.addr;if(k.label!=null){c=k.label;j='<span class="hidden-phone"> - '+k.addr+"</span>"}var b='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+c+"</a>"+j+" "+h+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(b)}d.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var m=[];var j=l.filteredTaints;for(var h in j){var n=j[h];var e=-1;for(var f in n){var d=n[f];for(var c in m){if(c!=e&&$.inArray(f,m[c])){if(e>=0){var g=m.splice(e,1);var k=m.splice(c,1);m.push(g.concat(k))}else{m[c].push(h)}e=c}if(e>=0){break}}}if(e==-1){m.push([h])}}buildTable(m);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(m)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};