function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var m=$("#active-addresses");var n=m.find("table");n.find("tbody").remove();for(var h in a){var e=a[h];if(h==0){n.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var c=$("<tbody></tbody>").appendTo(n);for(var l in e){var f=e[l];var g=addresses[f];if(!g){continue}var b="";if(g.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(g.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var j=g.addr;if(g.label!=null){j=g.label;k='<span class="hidden-phone"> - '+g.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+g.addr+'" onclick="showAddressModal(\''+g.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+g.addr+'" target="new">'+j+"</a>"+k+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(g.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';c.append(d)}n.append(c)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var c=[];var n=j.filteredTaints;for(var h in n){var k=n[h];var d=-1;for(var e in k){var m=k[e];for(var l in c){if(l!=d&&$.inArray(e,c[l])){if(d>=0){var g=c.splice(d,1);var f=c.splice(l,1);c.push(g.concat(f))}else{c[l].push(h)}d=l}if(d>=0){break}}}if(d==-1){c.push([h])}}buildTable(c);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};