function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(h){var f=$("#active-addresses");var k=f.find("table");k.find("tbody").remove();for(var c in h){var e=h[c];if(c==0){k.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(k);for(var d in e){var b=e[d];var m=addresses[b];if(!m){continue}var a="";if(m.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var g=m.addr;if(m.label!=null){g=m.label;j='<span class="hidden-phone"> - '+m.addr+"</span>"}var n='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+g+"</a>"+j+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(n)}k.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var g=[];var f=n.filteredTaints;for(var d in f){var j=f[d];var k=-1;for(var e in j){var m=j[e];for(var c in g){if(c!=k&&$.inArray(e,g[c])){if(k>=0){var l=g.splice(k,1);var h=g.splice(c,1);g.push(l.concat(h))}else{g[c].push(d)}k=c}if(k>=0){break}}}if(k==-1){g.push([d])}}buildTable(g);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};