function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var d=$("#active-addresses");var a=d.find("table");a.find("tbody").remove();for(var l in n){var b=n[l];if(l==0){a.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{a.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(a);for(var m in b){var j=b[m];var c=addresses[j];if(!c){continue}var g="";if(c.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var h=c.addr;if(c.label!=null){h=c.label;e='<span class="hidden-phone"> - '+c.addr+"</span>"}var f='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+h+"</a>"+e+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(f)}a.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var n=[];var m=g.filteredTaints;for(var k in m){var e=m[k];var h=-1;for(var j in e){var c=e[j];for(var l in n){if(l!=h&&$.inArray(j,n[l])){if(h>=0){var d=n.splice(h,1);var f=n.splice(l,1);n.push(d.concat(f))}else{n[l].push(k)}h=l}if(h>=0){break}}}if(h==-1){n.push([k])}}buildTable(n);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};