function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(h){var d=$("#active-addresses");var k=d.find("table");k.find("tbody").remove();for(var a in h){var e=h[a];if(a==0){k.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(k);for(var m in e){var b=e[m];var n=addresses[b];if(!n){continue}var g="";if(n.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(n.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var l=n.addr;if(n.label!=null){l=n.label;c='<span class="hidden-phone"> - '+n.addr+"</span>"}var f='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+n.addr+'" onclick="showAddressModal(\''+n.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+n.addr+'" target="new">'+l+"</a>"+c+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(n.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(f)}k.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var n=[];var g=d.filteredTaints;for(var k in g){var c=g[k];var e=-1;for(var l in c){var m=c[l];for(var j in n){if(j!=e&&$.inArray(l,n[j])){if(e>=0){var f=n.splice(e,1);var h=n.splice(j,1);n.push(f.concat(h))}else{n[j].push(k)}e=j}if(e>=0){break}}}if(e==-1){n.push([k])}}buildTable(n);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};